mapper.select:
select * from org_position where delflag='0'
and id=#{id}
and posno=#{posno}
and posname=#{posname}
limit 1

select * from org_position where delflag='0' 

    order by ${sort} 
    ${order}

limit #{begin}, #{limit}

mapper.update:
update org_position
posno=#{posno}, posname=#{posname}, remark=#{remark}, sortno=#{sortno}, modifytime=#{modifytime}, modifier=#{modifier},
where id = #{id}

update org_position set delflag='1'  
where id in

    #{item}
